var plant = function() {

	// MEMBER
	var self = this;

	self.state 			= null;
	self.lastWatering 	= null;
	self.humidity 		= null;
	self.mood			= null;

	
	// METHODS
	self.init = function() {
		self.mood = plant.LIST_HUMEUR.Contente;
	}


	self.setLastWatering = function(date) {
		self.lastWatering = date;
	}


	// INIT
	self.init();
};

plant.LIST_HUMEUR = {
	Contente 	: "Je suis contente",
	Colere		: "Je suis en colere"
}